mapper.select:
    select r.id as deliverId, r.jobId, c.name as companyName, j.jobName, r.createTime, r.status
    from RESUMEDELIVER r, JOB j, COMPANY c
    where r.jobId = j.id
    and j.companyId = c.id
    and r.resumeId = #{resumeId}

        and status = #{status}


limit #{start},#{size}

select count(j.id)
from RESUMEDELIVER r, JOB j, COMPANY c
where r.jobId = j.id
and j.companyId = c.id
and r.resumeId = #{resumeId}

   and status = #{status}

select j.id as jobId, u.userName, u.email, u.phone, r.id as resumeId, rd.status, u.id as userId
from JOB j, RESUMEDELIVER rd, RESUME r, USER u
where j.id = rd.jobId
and rd.resumeId = r.id
and r.userId = u.id
and j.id=#{jobId}
and u.userType=0

    and status = #{status}

limit #{start},#{size}

select count(id)
from RESUMEDELIVER
where resumeId = #{resumeId}
and jobId = #{jobId}
